+        break;
+      case 'expired':
+        whereClause.memberType = 'member';
+        whereClause.keyFob = null;
+        break;
+      case 'prospect':
+        whereClause.memberType = 'prospect';
+        break;
+      case 'recent':
+        const thirtyDaysAgo = new Date();
+        thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
+        whereClause.createdAt = { gte: thirtyDaysAgo };
+        break;
+      default:
